public class ComputeRectArea {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// Calculate the area of the rectangle
		double length;
		double width;
		double area;
				
		//Assign the length and width
		length = 10;
		width = 15.7;
				
		// Calculate the area
		area = length * width;
				
		System.out.println("Area of the rectangle with length " + length + " width " + width + " is " + area);

	}

}
